template<class T, size_t N>
concept has-tuple-element = exposition only
  requires(T t) {
    typename tuple_size<T>::type;
    requires N < tuple_size_v<T>;
    typename tuple_element_t<N, T>;
    { get<N>(t) } -> const tuple_element_t<N, T>&;
  };

template<class T, size_t N>
concept has-tuple-element = exposition only
