[…] template<class T> concept has-member-value-type = requires { typename T::value_type; }; // exposition only
template<class T> concept has-member-element-type = requires { typename T::element_type; }; // exposition only
template<class> struct indirectly_readable_traits { };
[…]
template<has-member-value-type T> struct indirectly_readable_traits<T> : cond-value-type<typename T::value_type> { };
template<has-member-element-type T> struct indirectly_readable_traits<T> : cond-value-type<typename T::element_type> { };
template<has-member-value-type T> requires has-member-element-type<T> struct indirectly_readable_traits<T> { };
template<has-member-value-type T> requires has-member-element-type<T> && same_as<remove_cv_t<typename T::element_type>, remove_cv_t<typename T::value_type>> struct indirectly_readable_traits<T> : cond-value-type<typename T::value_type> { }; […]
