namespace std::ranges {
  […]
  template<size_t N, class I, class S, subrange_kind K>
    requires ((N < 2== 0 && copyable<I>) || N == 1)
    constexpr auto get(const subrange<I, S, K>& r);

  template<size_t N, class I, class S, subrange_kind K>
    requires (N < 2)
    constexpr auto get(subrange<I, S, K>&& r);
}

if constexpr (N == 0)
  return r.begin();
else
  return r.end();
