using D = IOTA-DIFF-T(W);
if constexpr (is-integer-like<W>) {
  auto y_value = W(y.bound_);
  if constexpr (is-signed-integer-like<W>) {
    return D(D(x.value_) - D(y_value));
  } else {
    return (y_value > x.value_)
      ? D(-D(y_value - x.value_))
      : D(x.value_ - y_value);
