  template<input_range V, forward_range Pattern>
  requires view<V> && input_range<range_reference_t<V>>
        && view<Pattern> && compatible-joinable-ranges<range_reference_t<V>, Pattern>
  template<bool Const>
  class join_with_view<V, Pattern>::iterator {
