constexpr auto end() requires (!simple-view<V>) {
  if constexpr (forward_range<V> && common_range<V> && sized_range<V>)
    return iterator<false>(ranges::end(base_), ranges::distance(base_));
  else
    return sentinel<false>(ranges::end(base_));
}
