std::visit([&](const auto& it1, const auto& it2) {
    if constexpr (is_same_v<decltype(it1), decltype(it2)>) {
      ranges::iter_swap(it1, it2);
    } else {
      ranges::swap(*x, *y);
    }
  },
  x.it_, y.it_);
